void Biblioteca::ejecutarMapa() {

    // Verificar que los vectores tengan datos
    if (m_tilesBase.empty()) {
        std::cerr << "‚ùå ERROR: m_tilesBase est√° vac√≠o" << std::endl;
        return;
    }

    if (!m_tilemapBase.load("assets/Tilesets/libreria.png", sf::Vector2u(16, 16),
                          m_tilesBase, m_ancho, m_alto)) {
        std::cerr << "‚ùå ERROR cargando el tilemap base" << std::endl;
    }
}

void Biblioteca::actualizar() {
    m_personaje.actualizar(m_tilesBase, m_ancho, m_alto);
    m_personaje.setTilesValidos(m_tilesValidos);

    sf::FloatRect personajeBounds = m_personaje.obtenerHitbox();
    // 2. Obtener la caja de colisi√≥n (AABB) del √°rea de activaci√≥n.
    sf::FloatRect triggerArea = m_areaSalida.getGlobalBounds();

    // 3. üéØ SOLUCI√ìN FINAL VERIFICANDO ACCESO A PROPIEDADES (SFML 3.0+):
    // Usamos .position.x, .size.x, etc.
    bool colision = (personajeBounds.position.x < triggerArea.position.x + triggerArea.size.x) &&
                    (personajeBounds.position.x + personajeBounds.size.x > triggerArea.position.x) &&
                    (personajeBounds.position.y < triggerArea.position.y + triggerArea.size.y) &&
                    (personajeBounds.position.y + personajeBounds.size.y > triggerArea.position.y);

    if (colision) {
        gestor->empujarEstado(std::make_unique<Biblioteca>(gestor, m_window, m_personaje));
    }
}

Biblioteca::~Biblioteca() {

}

void Biblioteca::manejarEventos(sf::RenderWindow& window) {
    while (auto event = m_window.pollEvent()) {
        if (event->is<sf::Event::Closed>()) {
            m_window.close();
            return;
        }
    }
}

void Biblioteca::ejecutarBiblioteca() {
    bool saliendo = false;

    // Bucle de la pantalla de la Biblioteca.
    // Bloquea la ejecuci√≥n de MapaPrincipal hasta que 'saliendo' sea true.
    while (m_window.isOpen() && !saliendo) {

        // 1. Manejar Eventos (teclado, cierre de ventana, etc.)
        // La funci√≥n debe manejar eventos de la ventana, incluyendo la condici√≥n para salir.
        //saliendo = manejarEventos();

        // 2. Actualizar L√≥gica (movimiento del personaje, etc.)
        // Se actualiza la posici√≥n del personaje o los elementos interactivos de la biblioteca.
        actualizar();

        // 3. Dibujar
        m_window.clear(sf::Color::Black); // üëà ¬°CLAVE! Borra lo que dibuj√≥ MapaPrincipal.
        dibujar(m_window);                        // Dibuja el fondo de la biblioteca, el personaje, etc.
        m_window.display();               // Muestra el nuevo contenido de la Biblioteca.
    }

    // Si el bucle termina, el control regresa al MapaPrincipal.
}

void Biblioteca::dibujar(sf::RenderWindow& window) {
    // Usar la ventana miembro o el par√°metro, pero la firma debe ser correcta.
    window.draw(m_tilemapBase);
    window.draw(m_areaSalida);
    m_personaje.dibujar(window);
}

